import * as actionType from './actions';
const initialState = {
  value: 0,
  questionOne: {
    title: 'Email',
    response: ''
  },
  questionTwo: {
    title: 'Project Description',
    response: ''
  },
  questionThree: {
    title: 'Price Range',
    response: ''
  },
  questionFour: {
    title: 'Upload Files',
    response: []
  },
  questionFive: {
    title: 'Requested Services',
    response: []
  },
  responses: []
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionType.INCREASE:
      return {
        ...state,
        value: state.value + 1
      }
    case actionType.DECREASE:
      return {
        ...state,
        value: state.value - 1
      }
    case actionType.ZERO:
      return {
        ...state,
        value: 0
      }
    case actionType.UPDATESQUESTIONONE:
      return {
        ...state,
        questionOne: {
          ...questionOne,
          respnse: action.payload
        }
      }
    case actionType.UPDATESQUESTIONTWO:
      return {
        ...state,
        questionTwo: {
          ...questionTwo,
          response: action.payload
        }
      }
    case actionType.UPDATESQUESTIONTHREE:
      return {
        ...state,
        questionThree: {
          ...questionThree,
          response: action.payload
        }
      }
    case actionType.UPDATESQUESTIONFOUR:
      return {
        ...state,
        questionFour: {
          ...questionFour,
          response: [...state.questionFour, action.payload]
        }
      }
    case actionType.CLEARQUESTIONFOUR:
      return {
        ...state,
        questionFour: {
          ...questionFour,
          response: []
        }
      }
    case actionType.ADDTOQUESTIONFIVE:
      return {
        ...state,
        questionFive: {
          ...questionFive,
          response: [...state.questionFive, action.payload]
        }
      }
    case actionType.REMOVEFROMQUESTIONFIVE:
      return {
        ...state,
        questionFive: {
          ...questionFive,
          response: action.payload
        }
      }
    case actionType.CLEARQUESTIONFIVE:
      return {
        ...state,
        questionFive: {
          ...questionFive,
          response: []
        }
      }
    case actionType.UPDATERESPONSES:
      return {
        ...state,
        responses: [...state.responses, action.payload]
      }
    default:
      return state
  }
}
export default reducer;
